CREATE TABLE Classes (id INT AUTO_INCREMENT NOT NULL, course LONGTEXT NOT NULL COMMENT '(DC2Type:object)', startDate DATE NOT NULL, endDate DATE NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Address (id INT AUTO_INCREMENT NOT NULL, address1 VARCHAR(255) NOT NULL, address2 VARCHAR(255) DEFAULT NULL, city VARCHAR(255) NOT NULL, state VARCHAR(255) NOT NULL, country VARCHAR(255) NOT NULL, zip VARCHAR(255) NOT NULL, reference VARCHAR(255) NOT NULL, createdAt DATETIME NOT NULL, updatedAt DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE ClassAttendence (id INT AUTO_INCREMENT NOT NULL, class LONGTEXT NOT NULL COMMENT '(DC2Type:object)', client LONGTEXT NOT NULL COMMENT '(DC2Type:object)', createdAt DATETIME NOT NULL, updatedAt DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE client (id INT AUTO_INCREMENT NOT NULL, first_name VARCHAR(255) NOT NULL, last_name VARCHAR(255) NOT NULL, birthday DATE NOT NULL, fiscal_document VARCHAR(20) NOT NULL, telephone VARCHAR(15) NOT NULL, cellphone VARCHAR(15) NOT NULL, email VARCHAR(255) NOT NULL, courses LONGTEXT NOT NULL COMMENT '(DC2Type:array)', certifications LONGTEXT NOT NULL COMMENT '(DC2Type:array)', createdAt DATETIME NOT NULL, updatedAt DATETIME NOT NULL, INDEX client_name (first_name, last_name), INDEX idx_client_birthday (birthday), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE client_address (client_id INT NOT NULL, address_id INT NOT NULL, INDEX IDX_5F732BFC19EB6921 (client_id), INDEX IDX_5F732BFCF5B7AF75 (address_id), PRIMARY KEY(client_id, address_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Course (id INT AUTO_INCREMENT NOT NULL, course VARCHAR(255) NOT NULL, preRequisite LONGTEXT NOT NULL, createdAt DATETIME NOT NULL, updatedAt DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE client_address ADD CONSTRAINT FK_5F732BFC19EB6921 FOREIGN KEY (client_id) REFERENCES client (id) ON DELETE CASCADE;
ALTER TABLE client_address ADD CONSTRAINT FK_5F732BFCF5B7AF75 FOREIGN KEY (address_id) REFERENCES Address (id) ON DELETE CASCADE;
